import { useEffect, useState } from "react";

function Hello() {
  function byeFn() {
    console.log("bye!");
  };
  function hiFn() {
    console.log("Created!");
    return byeFn;
  };
  useEffect(hiFn, []);
  return <h1>Hello</h1>;
};

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => setShowing((prev) => !prev);
  return (
    <div>
      {showing ? <Hello /> : null}
      <button style={{
      color: "white",
      backgroundColor: "tomato",
      borderRadius: 25,
      fontSize: 50
      }}
      onClick={onClick}>{showing ? "Hide" : "Show"}</button>
    </div>
  );
};

export default App;
