import { useEffect, useState } from "react";

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => setShowing((prev) => !prev);
  return (
    <div>
     <button style={{
      color: "white",
      backgroundColor: "tomato",
      borderRadius: 5,
      fontSize: 50
      }}
      onClick={onClick}>{showing ? "Hide" : "Show"}</button>
    </div>
  );
};

export default App;
