import { useEffect, useState } from "react";

function App() {
  const [budget, setBudget] = useState(0);
  const onChange = (event) => {
    setBudget(event.target.value);
  };
  const [gain, setGain] = useState(0);
  const onSelect = (event) => {
    setGain(event.target.value.coin.quotes.USD.price);
    console.log(setGain);
  };
  const [loading,setLoading] = useState(true);
  const [coins, setCoins] = useState([]);
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((responce) => responce.json())
    .then((json) => {
      setCoins(json);
      setLoading(false);
    });
  }, []);
  return (
    <div>
      <h1>The Coins! {loading ? "" : `(${coins.length})`}</h1>
      <hr />
      <div>
        <label htmlFor="budget" >Budget(USD): </label>
        <input id="budget" type="number" value={budget} onChange={onChange} placeholder="How much do you have?" />
      </div>
      <div>
        <label htmlFor="amount" >Amount: </label>
        {coins.map((coin, index) => (<input key={index} id="amount" type="number" placeholder="You can get..." value={budget/coin.quotes.USD.price} onSelect={onSelect}/>))}
      </div>
      <br />
      {loading ? (
        <strong>Loading...</strong>
      ) : (
        <select onSelect={setGain}>
          {coins.map((coin, index) => (
            <option key={index}>
              {coin.name} ({coin.symbol}): ${Math.round(coin.quotes.USD.price)} USD
            </option>
          ))}
        </select>
      )}
    </div>
  );
};

export default App;
