import { useEffect, useState } from "react";

function App() {
  const [budget, setBudget] = useState("");
  const onChange = (event) => {
    setBudget(event.target.value);
  };
  const [gain, setGain] = useState("");
  const onSelect = (event) => {
    setGain(event.target.value);
  };
  const [loading,setLoading] = useState(true);
  const [coins, setCoins] = useState([]);
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
    .then((responce) => responce.json())
    .then((json) => {
      setCoins(json);
      setLoading(false);
    });
  }, []);
  return (
    <div>
      <h1>The Coins! {loading ? "" : `(${coins.length})`}</h1>
      <hr />
      <div>
        <label htmlFor="budget" >Budget(USD): </label>
        <input id="budget" type="number" value={budget} onChange={onChange} placeholder="How much do you have?" />
      </div>
      <div>
        <label htmlFor="amount" >Amount: </label>
        <input id="amount" type="number" value={gain > 0 ? (budget / gain).toFixed(2) : null} placeholder="You can get..." />
      </div>
      <br />
      {loading ? (
        <strong>Loading...</strong>
      ) : (
        <select onSelect={onSelect}>
          {coins.map((coin, index) => (
            <option key={index}>
              {coin.name} ({coin.symbol}): ${Math.round(coin.quotes.USD.price * 100) / 100} USD
            </option>
          ))}
        </select>
      )}
    </div>
  );
};

export default App;
